#include <stdio.h>

/*
6. Ваня Штерн очень любит следить за техническим прогрессом. Он пытается прогнозировать, до каких объемов выраст ет объем оперативной памяти компьютеров. Как известно, память имеет объем, равный степени двойки. Помогите Ване посчитать возможные объемы памяти. Реализуйте функцию std::string RamSize(unsigned N), которая вернет результат возведения двух в степень N. N < 20000. Например, при N = 1000 ответ будет 
*/

void RamSize(unsigned n,int*arr,int degree)
{
  for(int j=0;j<n;j++)
  {
    for(int i = 999 ;i>0;i--)
    {
      arr[i]*=degree;
      if(arr[i+1]>9)
      {
        arr[i] += arr[i+1]/10;
        arr[i+1] %= 10;
      }
    }
  }
}

int main()
{
  int arr[1001]={};
  arr[999]=1;
  int n;
  scanf("%i",&n);
  RamSize(n,arr,2);
  int bol=0;
  for(int i=0;i<1000;i++)
  {
    if(arr[i]!=0)
      bol=1;
    if(bol==1)
      printf("%i",arr[i]);
  }
}
